function initSliderImages() {
	var activeClass = 'val';
	var minValue = 3;
	var maxValue = 40;

	jQuery('.slider-images').each(function(){
		var holder = jQuery(this);
		var slider = holder.find('.slider-hold');

		var inputFrom = holder.find('.slider-input');
		var minCurVal = parseFloat(holder.find('.cur-val').val());
		var changeTimer = null;

		slider.slider({
			min: minValue,
			max: maxValue,
			step: 1,
			value: minCurVal,
			slide: function (event, ui) {
				inputFrom.val(ui.value);
			}
		});

		inputFrom.val(slider.slider('value'));

		inputFrom.on('keyup', function() {
			var numValFrom = parseFloat(inputFrom.val().replace(/[^0-9]+/g, ''));
			var slideVal = numValFrom;

			if (isNaN(numValFrom)) {
				slideVal = 0;
			} else {
				slideVal = Math.min(Math.max(slideVal, minValue), maxValue);				
				if (changeTimer) {
					clearTimeout(changeTimer);
					changeTimer = null;
				}
				changeTimer = setTimeout(function() {
					var newNumValFrom = parseFloat(inputFrom.val().replace(/[^0-9]+/g, ''));
					if (!isNaN(numValFrom) && newNumValFrom < minValue || newNumValFrom > maxValue) {
						inputFrom.val(slideVal);
					}

				}, 1000);
			}
			slider.slider('value', slideVal);
		});
	});
}


function initSliderJump() {
	var minValue = 5;
	var maxValue = 85;

	jQuery('.slider-size').each(function(){
		var holder = jQuery(this);
		var slider = holder.find('.slider-hold');
		var inputFrom = holder.find('.slider-input');
		var minCurVal = parseFloat(holder.find('.cur-val').val());
		var changeTimer = null;
		slider.slider({
			min: minValue,
			max: maxValue,
			step: 1,
			value: minCurVal,
			slide: function (event, ui) {
				inputFrom.val(ui.value);
			}
		});

		inputFrom.val(slider.slider('value'));

		inputFrom.on('keyup', function() {
			var numValFrom = parseFloat(inputFrom.val().replace(/[^0-9]+/g, ''));
			var slideVal = numValFrom;

			if (isNaN(numValFrom)) {
				slideVal = 0;
			} else {
				slideVal = Math.min(Math.max(slideVal, minValue), maxValue);				
				if (changeTimer) {
					clearTimeout(changeTimer);
					changeTimer = null;
				}
				changeTimer = setTimeout(function() {
					var newNumValFrom = parseFloat(inputFrom.val().replace(/[^0-9]+/g, ''));
					if (!isNaN(numValFrom) && newNumValFrom < minValue || newNumValFrom > maxValue) {
						inputFrom.val(slideVal);
					}

				}, 1000);
			}
			slider.slider('value', slideVal);
		});

	});
}

function initSliderRange() {
	var minValue = 50;
	var maxValue = 3840;

	jQuery('.range-section').each(function(){
		var holder = jQuery(this);
		var slider = holder.find('#slider-range');

		var inputFrom = holder.find('.input-from');
		var inputTo = holder.find('.input-to');


		var minCurVal = parseFloat(holder.find('.min-cur-val').val());
		var maxCurVal = parseFloat(holder.find('.max-cur-val').val());

		function kernel(x) {
			return Math.pow(x,0.25)
		}
		function invKernel(y) {
			return Math.pow(y,4);
		}
		var minL = kernel(minValue)
		var maxL = kernel(maxValue)
		function sliderToActual(val) {
			return Math.floor(0.1*invKernel((maxL-minL)*val/100 + minL))*10;
		}
		function actualToSlider(val) {
			return (kernel(val)-minL)*100/(maxL-minL);
		}

		slider.slider({
			range: true,
			min: 0,
			max: 100,
			step: 1,
			values: [actualToSlider(minCurVal), actualToSlider(maxCurVal)],
			slide: function (event, ui) {
				inputFrom.val(sliderToActual(ui.values[0]));
				inputTo.val(sliderToActual(ui.values[1]));
			}
		});

		inputFrom.val(minCurVal);
		inputTo.val(maxCurVal);

		inputFrom.on('keyup', function() {
			var numValFrom = parseFloat(inputFrom.val().replace(/[^0-9]+/g, ''));
			var numValTo = parseFloat(inputTo.val().replace(/[^0-9]+/g, ''));
			var slideVal = 0;

			if (isNaN(numValTo)) {
				numValTo = maxValue;
			}

			if (isNaN(numValFrom)) {
				numValFrom = minValue;
				slider.slider('values', 0, 0);
			}

			slideVal = numValFrom;

			if(numValFrom > numValTo) {
				slideVal = sliderToActual(slider.slider('values', 1));
				inputFrom.val(numValTo);
			} else if(numValFrom < minValue) {}

			slider.slider('values', 0, actualToSlider(slideVal));

		})



		inputTo.on('keyup', function() {
			var numValTo = parseFloat(inputTo.val().replace(/[^0-9]+/g, ''));
			var numValFrom = parseFloat(inputFrom.val().replace(/[^0-9]+/g, ''));
			var slideVal = 0;

			if (isNaN(numValFrom)) {
				numValFrom = minValue;
			}

			if (isNaN(numValTo)) {
				numValTo = maxValue;

				slider.slider('values', 1, 100);
			}

			slideVal = numValTo;

			if(numValFrom > numValTo) {
				slideVal = sliderToActual(slider.slider('values', 0));
				// inputTo.val(numValFrom);
			} else if(numValTo > maxValue) {
				slideVal = maxValue;
				inputTo.val(maxValue);
			}

			slider.slider('values', 1, actualToSlider(slideVal));

		});


	});
}

function intiBoottooltip() {
	jQuery('[data-toggle="tooltip"]').tooltip({
		trigger: 'click'
	});
}

